function init(){var e=new ymaps.Map("map",{center:[45.0411,38.9743],zoom:14});$.getJSON("json/theaters.json",function(t){for(clusterer=new ymaps.Clusterer,i=0;i<t.theaters.length;i++){var r=new ymaps.Placemark(JSON.parse(t.theaters[i].coord),{balloonContentBody:["<h3>"+t.theaters[i].name+"</h3><p><strong>Адрес:</strong> "+t.theaters[i].address+'</p><p><strong>Веб-сайт:</strong> <a href="'+t.theaters[i].website+'" target="_blank">'+t.theaters[i].website+"</a></p>"]},{preset:t.theaters[i].preset});clusterer.add(r)}e.geoObjects.add(clusterer)})}ymaps.ready(init);